class PrayerDetailScreen extends StatefulWidget {
  final String name;
  final String image;

  PrayerDetailScreen({
    required this.name,
    required this.image,
  });

  @override
  _PrayerDetailScreenState createState() => _PrayerDetailScreenState();
}

class _PrayerDetailScreenState extends State<PrayerDetailScreen> {
  @override
  Widget build(BuildContext context) {
    final appSettings = Provider.of<AppSettings>(context);
    final theme = appSettings.isDarkTheme ? ThemeData.dark() : ThemeData
        .light();
    final fontSize = appSettings.fontSize;
    final languageCode = appSettings.locale.languageCode;

    final localizedName = _getLocalizedPrayerName(widget.name, languageCode);
    final introText = _getIntroText(localizedName, languageCode);
    final prayerContent = _getPrayerContent(localizedName, languageCode);

    final isArabic = languageCode.startsWith('ar');
    final textDirection = isArabic ? TextDirection.rtl : TextDirection.ltr;

    return MaterialApp(
      theme: theme,
      home: Directionality(
        textDirection: textDirection,
        child: Scaffold(
          appBar: AppBar(
            title: Align(
              alignment: isArabic ? Alignment.centerRight : Alignment
                  .centerLeft,
              child: Text(localizedName),
            ),
            leading: Builder(
              builder: (BuildContext context) =>
                  IconButton(
                    icon: Icon(Icons.menu),
                    onPressed: () {
                      Scaffold.of(context).openDrawer(); // Open the AppDrawer
                    },
                  ),
            ),
          ),
          drawer: AppDrawer(),
          // Include the settings drawer
          drawerEdgeDragWidth: MediaQuery
              .of(context)
              .size
              .width,
          // Allow full edge swipe
          drawerScrimColor: Colors.black54,
          body: Stack(
            children: [
              ListView(
                padding: EdgeInsets.all(16.0),
                children: [
                  ClipRRect(
                    borderRadius: BorderRadius.circular(30.0),
                    child: Image.asset(
                      widget.image,
                      fit: BoxFit.cover,
                    ),
                  ),
                  SizedBox(height: 20),
                  Text(
                    introText,
                    style: TextStyle(
                      fontSize: fontSize,
                      fontWeight: FontWeight.bold,
                      color: Colors.blueAccent,
                    ),
                  ),
                  SizedBox(height: 20),
                  ExpansionTile(
                    initiallyExpanded: true,
                    title: Text(
                      isArabic ? 'تفاصيل الصلاة' : 'Prayer Details',
                      style: TextStyle(
                          fontSize: 24, fontWeight: FontWeight.bold),
                    ),
                    children: prayerContent.map((prayerItem) {
                      return GestureDetector(
                        onLongPress: () {
                          _showPsalmDialog(context, prayerItem['title']!,
                              prayerItem['text']!);
                        },
                        child: Card(
                          elevation: 4.0,
                          shape: RoundedRectangleBorder(
                            borderRadius: BorderRadius.circular(15.0),
                          ),
                          child: ExpansionTile(
                            title: GestureDetector(
                              onLongPress: () {
                                _showPsalmDialog(context, prayerItem['title']!,
                                    prayerItem['text']!);
                              },
                              child: Text(
                                prayerItem['title']!,
                                style: TextStyle(
                                  fontSize: 18,
                                  fontWeight: FontWeight.bold,
                                  color: Colors.red,
                                ),
                              ),
                            ),
                            children: [
                              Padding(
                                padding: const EdgeInsets.all(16.0),
                                child: GestureDetector(
                                  onLongPress: () {
                                    _showPsalmDialog(
                                        context, prayerItem['title']!,
                                        prayerItem['text']!);
                                  },
                                  child: Text(
                                    prayerItem['text']!,
                                    style: TextStyle(
                                      fontSize: fontSize,
                                    ),
                                  ),
                                ),
                              ),
                            ],
                          ),
                        ),
                      );
                    }).toList(),
                  ),
                  SizedBox(height: 20),
                ],
              ),
              Align(
                alignment: Alignment.topCenter,
                child: GestureDetector(
                  onVerticalDragEnd: (details) {
                    if (details.primaryVelocity! > 0) {
                      Navigator.of(context).pop(); // Pops the current screen
