  void _showPsalmDialog(BuildContext context, String title, String text) {
    double dialogFontSize = 16.0;
    bool _textSelected = false;

    showDialog(
      context: context,
      barrierDismissible: true,
      builder: (BuildContext context) {
        return StatefulBuilder(
          builder: (BuildContext context, StateSetter setState) {
            return AlertDialog(
              title: Text(
                title,
                style: TextStyle(
                  color: Colors.red,
                  fontWeight: FontWeight.bold,
                  fontSize: 20,
                ),
              ),
              content: SingleChildScrollView(
                child: Column(
                  mainAxisSize: MainAxisSize.min,
                  crossAxisAlignment: CrossAxisAlignment.start,
                  children: [
                    // Use a Theme to set the text selection color
                    Theme(
                      data: ThemeData(
                        textSelectionTheme: TextSelectionThemeData(
                          cursorColor: Colors.blue,
                          selectionColor: Colors.blue.withOpacity(0.5),
                          selectionHandleColor: Colors.blue,
                        ),
                      ),
                      child: SelectableText(
                        text,
                        style: TextStyle(fontSize: dialogFontSize),
                        scrollPhysics: ClampingScrollPhysics(),
                        showCursor: true,
                        onSelectionChanged: (selection, cause) {
                          // Set flag to indicate text is selected
                          _textSelected = true;
                        },
                        onTap: () {
                          // Ensure flag is reset if necessary
                          if (_textSelected) {
                            _textSelected = false;
                          }
                        },
                      ),
                    ),
                    SizedBox(height: 20),
                  ],
                ),
              ),
              actions: <Widget>[
                Row(
                  mainAxisAlignment: MainAxisAlignment.end,
                  children: [
                    IconButton(
                      icon: Icon(Icons.zoom_in),
                      onPressed: () {
                        setState(() {
                          dialogFontSize += 2.0;
                        });
                      },
                    ),
                    IconButton(
                      icon: Icon(Icons.zoom_out),
                      onPressed: () {
                        setState(() {
                          dialogFontSize = dialogFontSize > 10.0 ? dialogFontSize - 2.0 : dialogFontSize;
                        });
                      },
                    ),
                    IconButton(
                      icon: Icon(Icons.copy),
                      onPressed: () {
                        Clipboard.setData(ClipboardData(text: text)).then((_) {
                          // Delay showing the toast to ensure it appears
                          Future.delayed(Duration(milliseconds: 100), () {
                            if (_textSelected) {
                              Fluttertoast.showToast(
                                msg: 'Selection copied ✅',
                                toastLength: Toast.LENGTH_SHORT,
                                gravity: ToastGravity.BOTTOM,
                                timeInSecForIosWeb: 1,
                                backgroundColor: Colors.black54,
                                textColor: Colors.white,
                                fontSize: 16.0,
                              );
                              _textSelected = false; // Reset flag
                            }
                            Fluttertoast.showToast(
                              msg: 'Copied to clipboard ✅',
                              toastLength: Toast.LENGTH_SHORT,
                              gravity: ToastGravity.BOTTOM,
                              timeInSecForIosWeb: 1,
                              backgroundColor: Colors.black54,
                              textColor: Colors.white,
                              fontSize: 16.0,
                            );

                            Vibration.vibrate(); // Trigger vibration on copy
                          });
                        });
                      },
                    ),
                  ],
                ),
              ],
            );
          },
        );
      },
    );
  }
